mapper.insert:
INSERT INTO member(
username,
password, email, phone, role
) VALUES (
#{username}, #{password},
#{email}, #{phone}, #{role}
)

mapper.select:
SELECT id, username, password, email, phone, role
FROM member
WHERE username = #{username}

SELECT id
FROM member
WHERE username = #{username}

mapper.update:
UPDATE member
SET email = #{email}, phone = #{phone}
WHERE username = #{username}

UPDATE member
SET file_path = #{filePath}, org_name = #{orgName}, system_name = #{systemName}
WHERE member_id = #{memberId}
