mapper.insert:
INSERT INTO member(
username,
password, email, phone, total_dist, total_running_second, role
) VALUES (
#{username}, #{password},
#{email}, #{phone}, #{totalDist}, #{totalRunningSecond}, #{role}
)

mapper.select:
SELECT member_id, username, password, email, phone, total_dist, total_running_second, role
FROM member
WHERE username = #{username}

SELECT member_id
FROM member
WHERE username = #{username}

mapper.update:
UPDATE member
SET email = #{email}, phone = #{phone}
WHERE username = #{username}

UPDATE member
SET total_dist = #{totalDist}, total_running_second = #{totalRunningSecond}
WHERE username = #{username}

UPDATE member_file
SET file_path = #{filePath}, org_name = #{orgName}, system_name = #{systemName}
WHERE member_id = #{memberId}
